package com.chaintope.openassetsj.protocol;

import static org.junit.Assert.*;

import java.io.BufferedReader;
import java.io.FileReader;
import java.util.ArrayList;
import java.util.Collections;
import org.bitcoinj.script.Script;
import org.junit.Test;

import com.chaintope.openassetsj.utils.Utils;

/**
 * Validates the logic written in MarkerOutput
 */
public class MarkerOutputTest {

	ArrayList<Long> assetQuantities = new ArrayList<>();
	String metadata;
	MarkerOutput markerOutput;
	
	String expectedOutput;
	
	/**
	 * Method to test the functionality of toPayload()
	 */
	@Test
	public void toPayloadTest() {
		
		assetQuantities.add(10000L);
		metadata = "";
		markerOutput = new MarkerOutput(assetQuantities, metadata);
		String noMetadataPayload = markerOutput.toPayload();
		expectedOutput = "4f41010001904e00";
		assertEquals(expectedOutput, noMetadataPayload);

		reset();
		
		assetQuantities.add(10000L);
		metadata = "u=https://cpr.sm/5YgSU1Pg-q";
		markerOutput = new MarkerOutput(assetQuantities, metadata);
		String metadataPayload = markerOutput.toPayload();
		expectedOutput = "4f41010001904e1b753d68747470733a2f2f6370722e736d2f35596753553150672d71";
		assertEquals(expectedOutput, metadataPayload);

		reset();
		
		assetQuantities.addAll(Collections.nCopies(252, 4L));
		metadata = "";
		markerOutput = new MarkerOutput(assetQuantities, metadata);
		String payload252 = markerOutput.toPayload();
		expectedOutput = "";
		assertEquals(expectedOutput, payload252);
		
		reset();
		
		assetQuantities.addAll(Collections.nCopies(253, 4L));
		metadata = "";
		markerOutput = new MarkerOutput(assetQuantities, metadata);
		String payload253 = markerOutput.toPayload();
		expectedOutput = "";
		assertEquals(expectedOutput, payload253);
		
		reset();
		
		assetQuantities.addAll(Collections.nCopies(10000, 4L));
		metadata = "";
		markerOutput = new MarkerOutput(assetQuantities, metadata);
		String payload10000 = markerOutput.toPayload();
		assertEquals("", payload10000);
		
		reset();
		
		assetQuantities.addAll(Collections.nCopies(65535, 4L));
		metadata = "";
		markerOutput = new MarkerOutput(assetQuantities, metadata);
		String payload65535 = markerOutput.toPayload();
		expectedOutput = getExpectedOutputFromFile("payload_65535.txt");
		assertEquals(expectedOutput, payload65535);
		
		reset();
		
		assetQuantities.addAll(Collections.nCopies(65536, 4L));
		metadata = "";
		markerOutput = new MarkerOutput(assetQuantities, metadata);
		String payload65536 = markerOutput.toPayload();
		expectedOutput = getExpectedOutputFromFile("payload_65536.txt");
		assertEquals(expectedOutput, payload65536);
		
		reset();
	}

	/**
	 * Method to test the functionality of deserializePayload()
	 */
	@Test
	public void deserializePayloadTest() {
		
		markerOutput = new MarkerOutput();
		markerOutput = markerOutput.deserializePayload("4f41010001904e1b753d68747470733a2f2f6370722e736d2f35596753553150672d71");
		assertEquals(1, markerOutput.getAssetQuantities().size());
		assertEquals(10000L, markerOutput.getAssetQuantities().get(0).longValue());
		assertEquals("u=https://cpr.sm/5YgSU1Pg-q", markerOutput.getMetadata());
	}

	/**
	 * Method to test the functionality of buildScript()
	 */
	@Test
	public void buildScriptTest() {
		
		assetQuantities.add(10000L);
		metadata = "u=https://cpr.sm/5YgSU1Pg-q";
		markerOutput = new MarkerOutput(assetQuantities, metadata);
		Script script = markerOutput.buildScript();
		assertEquals(true, script.isOpReturn());
		String scriptData = Utils.packByteArrayToString(script.getChunks().get(1).data);
		assertEquals("4f41010001904e1b753d68747470733a2f2f6370722e736d2f35596753553150672d71", scriptData);
	}
	
	/**
	 * Method to test the functionality of parseScript()
	 */
	@Test
	public void parseScriptTest() {
		
		markerOutput = new MarkerOutput();
		String validScript = markerOutput.parseScript(Utils.packHexStringToBytes("6a084f41010002014400"));
		assertEquals("4f41010002014400", validScript);
		
		reset();
		
		markerOutput = new MarkerOutput();
		String noOAPMarker = markerOutput.parseScript(Utils.packHexStringToBytes("6a04deadbeef"));
		assertEquals("", noOAPMarker);
		
		reset();
		
		markerOutput = new MarkerOutput();
		String invalidVarInt = markerOutput.parseScript(Utils.packHexStringToBytes("6a224f41010001ee05753d68747470733a2f2f6370722e736d2f6d694c5a50484779782d"));
		assertEquals("", invalidVarInt);
	}
	
	/**
	 * Resets the class members for re-use
	 */
	private void reset() {
		
		assetQuantities.clear();
		metadata = null;
		markerOutput = null;
		expectedOutput = null;
	}
	
	/**
	 * Reads the expected output from given file
	 * @param filename File name to read the data
	 * @return String from a file
	 */
	private String getExpectedOutputFromFile(String filename) {

		String data = "";
		
		try {
			FileReader fileReader = 
                new FileReader("./src/test/java/com/chaintope/openassetsj/expectedOutputs/" + filename);

            BufferedReader bufferedReader = new BufferedReader(fileReader);
            data = bufferedReader.readLine();
            bufferedReader.close();   
        } catch (Exception e) {
			e.printStackTrace();
		}
		return data;
	}
}
